from django.contrib.auth import authenticate, get_user_modelfrom django.contrib.auth.forms import UserCreationForm, AuthenticationFormfrom kofe import modelsfrom django import formsfrom django.utils.translation import ugettext_lazy as _from django.utils.text import capfirstfrom kofe.models import Accountfrom kofeFast import settingsfrom django.utils.module_loading import import_stringimport djangoclass RegistrationForm(UserCreationForm):    email = forms.EmailField(max_length=60, help_text='Required. Add a valid email address')    first_name = forms.CharField(max_length=60, help_text='Required. Add a valid name')    last_name = forms.CharField(max_length=60, help_text='Required. Add a valid surname')    phone_number = forms.CharField(max_length=13, help_text='Required. Add a valid phone number')    class Meta:        model = Account        fields = ("email", "first_name", "last_name", "phone_number", "password1", "password2")