"""Формы для моделей"""from django import formsfrom django.contrib.auth.forms import UserCreationFormfrom kofe.models import Accountclass RegistrationForm(UserCreationForm):    """Форма регистрации нового пользователя"""    email = forms.EmailField(max_length=60, help_text='Required. Add a valid email address')    first_name = forms.CharField(max_length=60, help_text='Required. Add a valid name')    last_name = forms.CharField(max_length=60, help_text='Required. Add a valid surname')    phone_number = forms.CharField(max_length=13, help_text='Required. Add a valid phone number')    class Meta:        model = Account        fields = ("email", "first_name", "last_name", "phone_number", "password1", "password2")